// ============================
// INPUT SOURCES
// ============================
const indeed = $("indeed").all();
const linkedin = $("LinkedIn").all();
const glassdoor = $("glassdoor").all();

// ============================
// SAFE GET
// ============================
function get(obj, path, fallback = "") {
  try {
    return path.split(".").reduce((o, k) => o?.[k], obj) ?? fallback;
  } catch {
    return fallback;
  }
}

function valid(job) {
  return !!(
    job.jobTitle ||
    job.companyName ||
    job.applyLink
  );
}

// ============================
// INDEED
// ============================
function mapIndeed(item) {
  const j = item.json;

  return {
    source: "Indeed",
    jobTitle: j.title || "",
    companyName: j.companyName || "",
    companyUrl: j.companyUrl || get(j, "companyLinks.corporateWebsite", ""),
    location:
      get(j, "location.formattedAddressShort", "") ||
      get(j, "location.fullAddress", ""),
    applyLink: j.applyUrl || j.jobUrl || "",
    employmentType: Array.isArray(j.jobType)
      ? j.jobType.join(", ")
      : ""
  };
}

// ============================
// LINKEDIN âœ… FIXED
// ============================
function mapLinkedIn(item) {
  const j = item.json; // âœ… each item IS the job

  return {
    source: "LinkedIn",
    jobTitle: j.title || "",
    companyName: j.organization || "",
    companyUrl: j.organization_url || j.linkedin_org_url || "",
    location:
      j.locations_derived?.[0] ||
      j.cities_derived?.[0] ||
      "",
    applyLink: j.external_apply_url || j.url || "",
    employmentType: Array.isArray(j.employment_type)
      ? j.employment_type.join(", ")
      : ""
  };
}

// ============================
// GLASSDOOR âœ… UPDATED
// ============================
function mapGlassdoor(item) {
  const j = item.json;

  return {
    source: "Glassdoor",
    jobTitle: j.job_title || "",
    companyName: j.company_name || "",
    companyUrl: j.company_website || j.company_url || "",
    location:
      j.job_location?.unknown ||
      [j.job_location?.city, j.job_location?.country]
        .filter(Boolean)
        .join(", "),
    applyLink: j.job_url || "",
    employmentType: Array.isArray(j.job_job_types)
      ? j.job_job_types.join(", ")
      : ""
  };
}

// ============================
// BUILD RESULTS
// ============================
const results = [];

// INDEED
for (const i of indeed) {
  const r = mapIndeed(i);
  if (valid(r)) results.push(r);
}

// LINKEDIN âœ… DIRECT MAP
for (const l of linkedin) {
  const r = mapLinkedIn(l);
  if (valid(r)) results.push(r);
}

// GLASSDOOR
for (const g of glassdoor) {
  const r = mapGlassdoor(g);
  if (valid(r)) results.push(r);
}

// ============================
// RETURN TO N8N
// ============================
return results.map(r => ({
  json: r
}));
